mapper.select:
SELECT meterId , readDt , measurementPointId , meterSerial , captureDiv , MAX(meterValue1) as meterValueCh1 , MAX(meterValue2) as meterValueCh2 , MAX(meterValue3) as meterValueCh3 , MAX(meterValue4) as meterValueCh4 , MAX(meterValueInterval1) as meterValueIntervalCh1 , MAX(meterValueInterval2) as meterValueIntervalCh2 , MAX(meterValueInterval3) as meterValueIntervalCh3 , MAX(meterValueInterval4) as meterValueIntervalCh4 , meterStatus , lpInterval , locationId , locationNm , aptNo , aptNm , mtime , itime , deviceId , deviceSerial , regDt , updateDt FROM( SELECT COUNT(a.CHANNEL), a.METER_ID AS meterId , IFNULL(DATE_FORMAT(a.READ_DT, '%y%m%d%H%i%s'),'') AS readDt , a.MESUREMENT_POINT_ID AS measurementPointId , a.METER_SERIAL AS meterSerial , a.CAPTURED_DIV AS captureDiv , CASE WHEN CHANNEL = 'CH001' THEN a.METER_VALUE ELSE NULL END AS meterValue1 , CASE WHEN CHANNEL = 'CH002' THEN a.METER_VALUE ELSE NULL END AS meterValue2 , CASE WHEN CHANNEL = 'CH003' THEN a.METER_VALUE ELSE NULL END AS meterValue3 , CASE WHEN CHANNEL = 'CH004' THEN a.METER_VALUE ELSE NULL END AS meterValue4 , CASE WHEN CHANNEL = 'CH001' THEN a.METER_VALUE_INTERVAL ELSE NULL END AS meterValueInterval1 , CASE WHEN CHANNEL = 'CH002' THEN a.METER_VALUE_INTERVAL ELSE NULL END AS meterValueInterval2 , CASE WHEN CHANNEL = 'CH003' THEN a.METER_VALUE_INTERVAL ELSE NULL END AS meterValueInterval3 , CASE WHEN CHANNEL = 'CH004' THEN a.METER_VALUE_INTERVAL ELSE NULL END AS meterValueInterval4 , a.METER_STATUS AS meterStatus , a.LP_INTERVAL AS lpInterval , a.LOCATION_ID AS locationId , FNC_GET_LOCATION_NM(a.LOCATION_ID) AS locationNm , a.APT_NO AS aptNo , b.APT_NM AS aptNm , DATE_FORMAT(a.MTIME, '%y%m%d%H%i%s') AS mtime , DATE_FORMAT(a.ITIME, '%y%m%d%H%i%s') AS itime , a.DEVICE_ID AS deviceId , a.DEVICE_SERIAL AS deviceSerial , DATE_FORMAT(a.REG_DT, '%y%m%d%H%i%s') as regDt , DATE_FORMAT(a.UPDATE_DT, '%y%m%d%H%i%s') as updateDt FROM gnd_meter_value a LEFT OUTER JOIN gnd_apt_info b ON a.APT_NO = b.APT_NO WHERE 1=1
AND a.METER_ID = #{meterId}
AND a.READ_DT BETWEEN #{startDate} AND #{endDate}
GROUP BY a.METER_ID , IFNULL(DATE_FORMAT(a.READ_DT, '%y-%m-%d %H:%i:%s'),'') , a.MESUREMENT_POINT_ID, a.METER_SERIAL, a.CAPTURED_DIV , CASE WHEN CHANNEL = 'CH001' THEN a.METER_VALUE ELSE NULL END , CASE WHEN CHANNEL = 'CH002' THEN a.METER_VALUE ELSE NULL END , CASE WHEN CHANNEL = 'CH003' THEN a.METER_VALUE ELSE NULL END , CASE WHEN CHANNEL = 'CH004' THEN a.METER_VALUE ELSE NULL END , CASE WHEN CHANNEL = 'CH001' THEN a.METER_VALUE_INTERVAL ELSE NULL END , CASE WHEN CHANNEL = 'CH002' THEN a.METER_VALUE_INTERVAL ELSE NULL END , CASE WHEN CHANNEL = 'CH003' THEN a.METER_VALUE_INTERVAL ELSE NULL END , CASE WHEN CHANNEL = 'CH004' THEN a.METER_VALUE_INTERVAL ELSE NULL END , a.METER_STATUS, a.LP_INTERVAL, a.LOCATION_ID, FNC_GET_LOCATION_NM(a.LOCATION_ID) , a.APT_NO, b.APT_NM , DATE_FORMAT(a.MTIME, '%y%m%d%H%i%s') , DATE_FORMAT(a.ITIME, '%y%m%d%H%i%s') , a.DEVICE_ID, a.DEVICE_SERIAL , DATE_FORMAT(a.REG_DT, '%y%m%d%H%i%s') , DATE_FORMAT(a.UPDATE_DT, '%y%m%d%H%i%s') ORDER BY readDt, channel ) A GROUP BY meterId , readDt , measurementPointId , meterSerial , captureDiv , meterStatus , lpInterval , locationId , locationNm , aptNo , aptNm , mtime , itime , deviceId , deviceSerial
